import React, { Component } from "react"
import { Howl } from "howler"

export class Radio extends Component {
  state = {
    radioStations: [
      { name: "Galaxy 92 FM", src: "http://galaxy.live24.gr:80/galaxy9292" },
      { name: "Kids Radio", src: "www.fake.url" },
    ],
  }
  render() {
    return (
      <div>
        <p>List of Radio Stations</p>
        <ul>
          {this.state.radioStations.map((radioStations) => (
            <li onClick={this.handleRadio.bind(this)}>{radioStations.name}</li>
          ))}
        </ul>
      </div>
    )
  }
  handleRadio() {
    console.log("radio press");
    //sound.
    var sound = new Howl({
      src: ["http://galaxy.live24.gr:80/galaxy9292"],
      html5: true,
    })

    sound.play()
  }
}

export default Radio
