import React, { Component } from "react"
// const clockStyle = {
//     color: "white"
// }

export class Clock extends Component {
  render() {
    return (
      <div>
        <h1>TimeNow :{this.getTimeString()}</h1>
      </div>
    )
  }
  getTimeString = () => {
    let date = new Date()
    setInterval(() => {
      return date.toLocaleTimeString()
    }, 1000)
  }
}

export default Clock
